import React, { Component } from 'react';
import {
  AppRegistry,
  StyleSheet,
  Text,
  TabBarIOS,
  View
} from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';

import Home from './Home';
import Record from './Record';
import Profile from './Profile';

var base64Icon = 'data:image/png;base64,';

export default class John extends Component {
  static title = 'John';
  static description = 'Diabetes Companion App';
  static displayName = 'John';

  state = {
    selectedTab: 'home',
    presses: 0,
  };

  _renderContent = (color: string, pageText: string, num?: number) => {
    return (
      <View style={[styles.tabContent, {backgroundColor: color}]}>
        <Text style={styles.tabText}>{pageText}</Text>
      </View>
    );
  };

  _home = () => {
    return (
      <Home />
    )
  }

  _record = () => {
    return (
      <Record />
    )
  }

  _profile = () => {
    return (
      <Profile />
    )
  }

  render() {
    return (
      <TabBarIOS>
        <Icon.TabBarItem
          title = "Home"
          iconName = "ios-home-outline"
          selectedIconName = "ios-home"
          selected={this.state.selectedTab === 'home'}
          onPress={() => {
            this.setState({
              selectedTab: 'home',
            });
          }}>
          {this._home()}
        </Icon.TabBarItem>
        <Icon.TabBarItem
          title = "Record"
          iconName = "ios-add-outline"
          selectedIconName = "ios-add"
          selected={this.state.selectedTab === 'record'}
          onPress={() => {
            this.setState({
              selectedTab: 'record',
            });
          }}>
          {this._record()}
        </Icon.TabBarItem>
        <Icon.TabBarItem
          title = "Profile"
          iconName = "ios-person-outline"
          selectedIconName = "ios-person"
          selected={this.state.selectedTab === 'profile'}
          onPress={() => {
            this.setState({
              selectedTab: 'profile',
            });
          }}>
          {this._profile()}
        </Icon.TabBarItem>
      </TabBarIOS>
    );
  }
}

const styles = StyleSheet.create({
  tabContent: {
    flex: 1,
    alignItems: 'center',
  },
  tabText: {
    color: 'white',
    margin: 50,
  },
});

AppRegistry.registerComponent('John', () => John);
